(242, 1, '2024-10-21 21:49:21', '2024-10-21 21:49:21', '<!-- wp:cover {\"url\":\"https://wbcmuaythai.com.mx/wp-content/uploads/2024/10/young-woman-boxer-training-gym-scaled-1-1024x682.jpg\",\"id\":235,\"dimRatio\":50,\"minHeight\":493,\"minHeightUnit\":\"px\",\"contentPosition\":\"center center\",\"align\":\"full\",\"style\":{\"color\":{\"duotone\":[\"rgb(19,54,40)\",\"#ffffff\"]}}} -->\n<div class=\"wp-block-cover alignfull\" style=\"min-height:493px\"><span aria-hidden=\"true\" class=\"wp-block-cover__background has-background-dim\"></span><img class=\"wp-block-cover__image-background wp-image-235\" alt=\"\" src=\"https://wbcmuaythai.com.mx/wp-content/uploads/2024/10/young-woman-boxer-training-gym-scaled-1-1024x682.jpg\" data-object-fit=\"cover\"/><div class=\"wp-block-cover__inner-container\"><!-- wp:paragraph {\"fontSize\":\"large\",\"fontFamily\":\"Rubik\",\"customFontSize\":100,\"lineHeight\":3,\"letterSpacing\":3,\"fontWeight\":\"900\",\"textTransform\":\"uppercase\",\"noBottomSpacing\":true,\"noTopSpacing\":true} -->\n<p class=\"has-rubik-font-family has-large-font-size has-custom-font has-custom-weight has-custom-transform has-custom-lineheight has-custom-letterspacing mb-0 pb-0 mt-0 pt-0\" style=\"font-family:Rubik;line-height:3;letter-spacing:3px;font-weight:900;text-transform:uppercase;font-size:100px\"><span class=\"uppercase\"><strong>committees</strong></span></p>\n<!-- /wp:paragraph --></div></div>\n<!-- /wp:cover -->\n\n<!-- wp:heading {\"level\":6,\"fontFamily\":\"Rubik\"} -->\n<h6 class=\"wp-block-heading has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><mark style=\"background-color:rgba(0, 0, 0, 0);color:#000000\" class=\"has-inline-color\">WBC MUAYTHAI OPERATIONS STRUCTURE</mark></h6>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\">The below committees are responsible for the operations of the World Boxing Council MuayThai (WBC MuayThai) for the primary purpose of promoting MuayThai around the world, to the highest standard possible, and to oversee MuayThai competitions in accordance with the official rules, regulations, and safety standards of the World Boxing Council MuayThai committee.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":6,\"fontFamily\":\"Rubik\"} -->\n<h6 class=\"wp-block-heading has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\">1. EXECUTIVE COMMITTEE</h6>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong><mark style=\"background-color:rgba(0, 0, 0, 0);color:#000000\" class=\"has-inline-color\">LIFETIME</mark> <mark style=\"background-color:rgba(0, 0, 0, 0);color:#000000\" class=\"has-inline-color\">PRESIDENT</mark></strong><br>Police General Kovid Bhakdibhumi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>PRESIDENT</strong><br>Colonel Thanapol Bhakdibhumi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>SECRETARY-GENERAL</strong><br>Mr. Kevin P. Noone</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>DEPUTY SECRETARY GENERAL</strong><br>Dr. Prechaporn Suwatnodom</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>HONORARY VICE PRESIDENT</strong><br>Mr. Brian Marcar</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>PRESIDENTS ADVISORS<br></strong>Mr. Patrick Cusick<br>Mr. Daniel Suarez</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>EXECUTIVE ADVISORS</strong><br>Ms. Chisa Yamane<br>Mr. Nash Keshwala<br>Mr. Hadj Bettahar<br>Mr. Max Baggio<br>Mr. Julio Gamboa</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\">Patrick Rivera </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":6,\"fontFamily\":\"Rubik\"} -->\n<h6 class=\"wp-block-heading has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\">2. CHAMPIONSHIP COMMITTEE</h6>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>CHAIRMAN</strong><br>Colonel Thanapol Bhakdibhumi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>VICE CHAIRMAN</strong><br>Mr. Kevin P. Noone</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":6,\"fontFamily\":\"Rubik\"} -->\n<h6 class=\"wp-block-heading has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\">3. RATINGS COMMITTEE</h6>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>CHAIRMAN</strong><br>Colonel Thanapol Bhakdibhumi</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>VICE CHAIRMEN</strong><br>Mr. Kevin P. Noone<br>Mr. Poptorn Roongsamai</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>MEMBERS</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\">Tim Dharmajiva <br>Mr. Pornchai Udomsomporn<br>Mr. Danuchkarn Onwimon<br>Mr. Apichet Pimkae<br>Colonel Ohm Patchakapati                                                                                                                 Tristian Dharmajiva </p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>SECRETARY</strong><br>Ms. Roselyn Lilaruchiwong</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":6,\"fontFamily\":\"Rubik\"} -->\n<h6 class=\"wp-block-heading has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\">4. RING OFFICIALS COMMITTEE</h6>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>CHAIRMAN</strong><br>Mr. Thawuth Pluemsamran</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>VICE CHAIRMAN</strong><br>Mr. Parin Hantanabool</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>MEMBERS</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\">Phanu Kusolwong &nbsp;– Nash Keshwala – Soraya Hamami – Reza Djabbarpoor &nbsp;</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"fontFamily\":\"Rubik\"} -->\n<p class=\"has-rubik-font-family has-custom-font\" style=\"font-family:Rubik\"><strong>SECRETARY</strong><br>Ms. Roselyn Lilaruchiwong</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:heading {\"level\":6,\"fontFamily\":\"Rubi