INSERT INTO `wp_cnp1njxsjf_wc_admin_notes` (`note_id`, `name`, `type`, `locale`, `title`, `content`, `content_data`, `status`, `source`, `date_created`, `date_reminder`, `is_snoozable`, `layout`, `image`, `is_deleted`, `is_read`, `icon`) VALUES
(1, 'wc-refund-returns-page', 'info', 'en_US', 'Configura una página de política de devoluciones y reembolsos para aumentar la credibilidad de tu tienda.', 'Hemos creado un borrador de muestra de la página de política de devoluciones y reembolsos. Echa un vistazo y modifícala según las necesidades de tu tienda.', '{}', 'unactioned', 'woocommerce-core', '2024-10-07 22:12:33', NULL, 0, 'plain', '', 0, 0, 'info'),
(2, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Cómo personalizar tu catálogo de productos', 'Quieres que tu catálogo de productos y tus imágenes tengan un aspecto estupendo y estén en consonancia con tu marca. Esta guía te dará todos los consejos que necesitas para que tus productos se vean bien en tu tienda.', '{}', 'unactioned', 'woocommerce-admin', '2024-10-08 22:55:05', NULL, 0, 'plain', '', 0, 0, 'info'),
(3, 'wc-admin-launch-checklist', 'info', 'en_US', '¿Estás preparado para lanzar tu tienda?', 'Para asegurarnos de que nunca tengas esa sensación de «¿Qué se me olvidó?» hemos preparado la lista de comprobación esencial de despegue.', '{}', 'unactioned', 'woocommerce-admin', '2024-10-08 22:55:05', NULL, 0, 'plain', '', 0, 0, 'info'),
(4, 'wc-admin-mobile-app', 'info', 'en_US', 'Instala la aplicación móvil WooCommerce', 'Instala la aplicación móvil de WooCommerce para gestionar pedidos, recibir avisos de ventas y ver métricas clave – allí donde estés.', '{}', 'unactioned', 'woocommerce-admin', '2024-10-09 23:08:04', NULL, 0, 'plain', '', 0, 0, 'info'),
(5, 'wc-admin-onboarding-payments-reminder', 'info', 'en_US', '¡Empieza a aceptar pagos en tu tienda!', 'Acepta pagos mediante el proveedor adecuado para ti - elige entre más de 100 pasarelas de pago para WooCommerce.', '{}', 'unactioned', 'woocommerce-admin', '2024-10-12 23:42:51', NULL, 0, 'plain', '', 0, 0, 'info'),
(6, 'wc-admin-usage-tracking-opt-in', 'info', 'en_US', 'Ayuda a WooCommerce a mejorar con el seguimiento de uso', 'Recopilar datos de uso nos permite mejorar WooCommerce. Se tendrá en cuenta a tu tienda a medida que evaluamos nuevas características, juzgamos la calidad de una actualización o determinamos si una mejora tiene sentido. Siempre puedes visitar los <a href=\"https://wbcmuaythai.com.mx/wp-admin/admin.php?page=wc-settings&#038;tab=advanced&#038;section=woocommerce_com\" target=\"_blank\">Ajustes</a> y elegir dejar de compartir datos. <a href=\"https://woocommerce.com/usage-tracking?utm_medium=product\" target=\"_blank\">Lee más</a> sobre los datos que recopilamos.', '{}', 'unactioned', 'woocommerce-admin', '2024-10-14 23:59:32', NULL, 0, 'plain', '', 0, 0, 'info');
